import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Typography, 
  Container, 
  Grid,
  Button,
  TextField,
  CircularProgress,
  Divider,
  Chip,
  Alert,
  List,
  ListItem,
  ListItemText,
  Paper
} from '@mui/material';
import GlassCard from '../components/GlassCard';
import { useNavigate } from 'react-router-dom';
import { ethers } from 'ethers';

const ShadowAccountPage = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [seedWallet, setSeedWallet] = useState(null);
  const [shadowWallet, setShadowWallet] = useState(null);
  const [shadowAccounts, setShadowAccounts] = useState([]);
  const [status, setStatus] = useState('');
  const [isCertified, setIsCertified] = useState(false);

  // 检查本地存储中是否有根账户信息
  useEffect(() => {
    const storedData = localStorage.getItem('signatureDetails');
    if (storedData) {
      try {
        const parsedData = JSON.parse(storedData);
        if (parsedData.seedAddress) {
          setSeedWallet({ address: parsedData.seedAddress });
          setIsCertified(true);
        }
      } catch (error) {
        console.error('解析存储数据失败:', error);
      }
    }

    // 加载已保存的影子账户
    const storedShadowAccounts = localStorage.getItem('shadowAccounts');
    if (storedShadowAccounts) {
      try {
        setShadowAccounts(JSON.parse(storedShadowAccounts));
      } catch (error) {
        console.error('解析影子账户数据失败:', error);
      }
    }
  }, []);

  // 创建影子账户
  const createShadowAccount = () => {
    if (!isCertified) {
      setStatus('请先完成根账户认证');
      return;
    }

    setLoading(true);
    setStatus('正在创建影子账户...');

    setTimeout(() => {
      try {
        // 创建随机钱包作为影子账户
        const wallet = ethers.Wallet.createRandom();
        setShadowWallet(wallet);
        setStatus('影子账户创建成功！');
        setLoading(false);
      } catch (error) {
        console.error('创建影子账户失败:', error);
        setStatus('创建影子账户失败: ' + error.message);
        setLoading(false);
      }
    }, 1500);
  };

  // 关联影子账户
  const linkShadowAccount = () => {
    if (!shadowWallet) {
      setStatus('请先创建影子账户');
      return;
    }

    setLoading(true);
    setStatus('正在生成环签名...');

    // 模拟环签名生成过程
    setTimeout(() => {
      setStatus('正在发送交易...');

      setTimeout(() => {
        setStatus('交易已发送，等待确认...');

        setTimeout(() => {
          // 添加到影子账户列表
          const newAccount = {
            address: shadowWallet.address,
            privateKey: shadowWallet.privateKey,
            createdAt: new Date().toISOString(),
            keyImage: '0x' + Math.random().toString(16).substr(2, 40)
          };

          const updatedAccounts = [...shadowAccounts, newAccount];
          setShadowAccounts(updatedAccounts);

          // 保存到本地存储
          localStorage.setItem('shadowAccounts', JSON.stringify(updatedAccounts));

          setStatus('影子账户关联成功！');
          setShadowWallet(null); // 重置当前影子账户
          setLoading(false);
        }, 1500);
      }, 1500);
    }, 1500);
  };

  return (
    <Box sx={{ 
      minHeight: '100vh',
      background: 'radial-gradient(circle at 50% 50%, #1a1b3a 0%, #0a0b1e 100%)',
      pt: 10,
      pb: 8
    }}>
      <Container>
        <Typography 
          variant="h4" 
          component="h1"
          sx={{ 
            fontWeight: 'bold',
            mb: 4,
            textAlign: 'center',
            background: 'linear-gradient(45deg, #ff00a0, #ff0058)',
            backgroundClip: 'text',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
          }}
        >
          影子账户管理
        </Typography>

        {!isCertified ? (
          <GlassCard>
            <Typography variant="h6" sx={{ mb: 3, textAlign: 'center' }}>
              请先完成根账户认证
            </Typography>
            <Typography variant="body1" sx={{ mb: 3, textAlign: 'center' }}>
              您需要先创建并认证根账户，才能管理影子账户。
            </Typography>
            <Button
              variant="contained"
              color="primary"
              fullWidth
              onClick={() => navigate('/seed-account')}
            >
              前往根账户认证
            </Button>
          </GlassCard>
        ) : (
          <Grid container spacing={4}>
            <Grid item xs={12} md={6}>
              <GlassCard>
                <Typography variant="h6" sx={{ mb: 3 }}>
                  创建新的影子账户
                </Typography>

                {shadowWallet ? (
                  <Box sx={{ mb: 3 }}>
                    <TextField
                      label="影子账户地址"
                      value={shadowWallet.address}
                      fullWidth
                      margin="normal"
                      InputProps={{
                        readOnly: true,
                      }}
                    />
                    <TextField
                      label="私钥"
                      value={shadowWallet.privateKey}
                      fullWidth
                      margin="normal"
                      InputProps={{
                        readOnly: true,
                      }}
                      type="password"
                    />
                    <Alert severity="warning" sx={{ mt: 2 }}>
                      请安全保存您的私钥，不要分享给任何人！
                    </Alert>
                  </Box>
                ) : (
                  <Button
                    variant="contained"
                    color="secondary"
                    fullWidth
                    onClick={createShadowAccount}
                    disabled={loading}
                    sx={{ mb: 3 }}
                  >
                    {loading ? <CircularProgress size={24} /> : '创建影子账户'}
                  </Button>
                )}

                {shadowWallet && (
                  <Button
                    variant="contained"
                    color="primary"
                    fullWidth
                    onClick={linkShadowAccount}
                    disabled={loading}
                    sx={{ mt: 3 }}
                  >
                    {loading ? <CircularProgress size={24} /> : '关联影子账户'}
                  </Button>
                )}

                {status && (
                  <Typography 
                    variant="body2" 
                    sx={{ 
                      mt: 2, 
                      textAlign: 'center',
                      color: status.includes('成功') ? 'success.main' : 'info.main'
                    }}
                  >
                    {status}
                  </Typography>
                )}
              </GlassCard>

              <GlassCard sx={{ mt: 4 }}>
                <Typography variant="h6" sx={{ mb: 3 }}>
                  影子账户说明
                </Typography>
                <Typography variant="body2" sx={{ mb: 2 }}>
                  影子账户是与您的根账户关联的匿名账户。通过环签名技术，这种关联在区块链上是不可追踪的。
                </Typography>
                <Typography variant="body2">
                  您可以使用影子账户进行完全匿名的交易，保护您的财务隐私。每个影子账户只能使用一次，以防止关联分析攻击。
                </Typography>
              </GlassCard>
            </Grid>

            <Grid item xs={12} md={6}>
              <GlassCard>
                <Typography variant="h6" sx={{ mb: 3 }}>
                  我的影子账户
                </Typography>

                {shadowAccounts.length > 0 ? (
                  <List sx={{ width: '100%' }}>
                    {shadowAccounts.map((account, index) => (
                      <Paper 
                        key={index} 
                        elevation={0}
                        sx={{ 
                          mb: 2, 
                          p: 2, 
                          background: 'rgba(255, 255, 255, 0.05)',
                          borderRadius: 2
                        }}
                      >
                        <Typography variant="subtitle2" sx={{ fontWeight: 'bold' }}>
                          影子账户 #{index + 1}
                        </Typography>
                        <Typography variant="body2" sx={{ wordBreak: 'break-all', mb: 1 }}>
                          <strong>地址:</strong> {account.address}
                        </Typography>
                        <Typography variant="body2" sx={{ mb: 1 }}>
                          <strong>创建时间:</strong> {new Date(account.createdAt).toLocaleString('zh-CN', { timeZone: 'Asia/Shanghai' })}
                        </Typography>
                        <Typography variant="body2" sx={{ mb: 1 }}>
                          <strong>密钥映像:</strong> {account.keyImage.substring(0, 10)}...
                        </Typography>
                        <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 1 }}>
                          <Button 
                            size="small" 
                            variant="outlined" 
                            color="primary"
                            onClick={() => {
                              navigator.clipboard.writeText(account.address);
                              setStatus('地址已复制到剪贴板');
                              setTimeout(() => setStatus(''), 2000);
                            }}
                          >
                            复制地址
                          </Button>
                        </Box>
                      </Paper>
                    ))}
                  </List>
                ) : (
                  <Typography variant="body1" sx={{ color: 'text.secondary', fontStyle: 'italic', textAlign: 'center' }}>
                    您还没有创建任何影子账户
                  </Typography>
                )}
              </GlassCard>

              <GlassCard sx={{ mt: 4 }}>
                <Typography variant="h6" sx={{ mb: 3 }}>
                  环签名交易
                </Typography>
                <Typography variant="body2" sx={{ mb: 2 }}>
                  使用影子账户进行交易时，系统会自动生成环签名，确保交易的匿名性和不可追踪性。
                </Typography>
                <Typography variant="body2" sx={{ mb: 3 }}>
                  每次交易都会使用不同的环和不同的密钥映像，进一步增强隐私保护。
                </Typography>

                <Button
                  variant="outlined"
                  color="secondary"
                  fullWidth
                  onClick={() => navigate('/')}
                >
                  返回首页
                </Button>
              </GlassCard>
            </Grid>
          </Grid>
        )}
      </Container>
    </Box>
  );
};

export default ShadowAccountPage;
