import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import './App.css';
import SeedAccountPage from './pages/SeedAccountPage';
import ShadowAccountPage from './pages/ShadowAccountPage';
import HomePage from './pages/HomePage';

function App() {
  // 存储应用状态
  const [seedWallet, setSeedWallet] = useState(null);
  const [soulWallet, setSoulWallet] = useState(null);
  const [isCertified, setIsCertified] = useState(false);

  // 从本地存储加载数据
  useEffect(() => {
    const storedSeedWallet = localStorage.getItem('seedWallet');
    const storedSoulWallet = localStorage.getItem('soulWallet');
    const storedCertification = localStorage.getItem('certificationDetails');

    if (storedSeedWallet) setSeedWallet(JSON.parse(storedSeedWallet));
    if (storedSoulWallet) setSoulWallet(JSON.parse(storedSoulWallet));
    if (storedCertification) setIsCertified(true);
  }, []);

  // 更新钱包状态并保存到本地存储
  const updateSeedWallet = (wallet) => {
    setSeedWallet(wallet);
    localStorage.setItem('seedWallet', JSON.stringify(wallet));
  };

  const updateSoulWallet = (wallet) => {
    setSoulWallet(wallet);
    localStorage.setItem('soulWallet', JSON.stringify(wallet));
  };

  const updateCertification = (isSuccess, details) => {
    setIsCertified(isSuccess);
    if (isSuccess && details) {
      localStorage.setItem('certificationDetails', JSON.stringify(details));
    } else {
      localStorage.removeItem('certificationDetails');
    }
  };

  return (
    <BrowserRouter>
      <div className="App">
        <nav className="App-nav">
          <div className="nav-logo">环签名隐私系统</div>
          <div className="nav-links">
            <Link to="/" className="nav-link">首页</Link>
            <Link to="/seed-account" className="nav-link">根账户</Link>
            <Link to="/shadow-account" className="nav-link">影子账户</Link>
          </div>
        </nav>

        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route 
            path="/seed-account" 
            element={
              <SeedAccountPage 
                seedWallet={seedWallet}
                soulWallet={soulWallet}
                isCertified={isCertified}
                updateSeedWallet={updateSeedWallet}
                updateSoulWallet={updateSoulWallet}
                updateCertification={updateCertification}
              />
            } 
          />
          <Route 
            path="/shadow-account" 
            element={
              <ShadowAccountPage 
                seedWallet={seedWallet}
                soulWallet={soulWallet}
                isCertified={isCertified}
              />
            } 
          />
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
