import React, { useState } from 'react';
import { 
  Box, 
  Typography, 
  Container, 
  Grid,
  Button,
  TextField,
  CircularProgress,
  Divider,
  Chip,
  Alert
} from '@mui/material';
import GlassCard from '../components/GlassCard';
import { useNavigate } from 'react-router-dom';
import { ethers } from 'ethers';

const SeedAccountPage = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [seedWallet, setSeedWallet] = useState(null);
  const [soulWallet, setSoulWallet] = useState(null);
  const [isCertified, setIsCertified] = useState(false);
  const [status, setStatus] = useState('');

  // 创建根账户
  const createSeedAccount = () => {
    setLoading(true);
    setStatus('正在创建根账户...');

    setTimeout(() => {
      try {
        // 创建随机钱包
        const wallet = ethers.Wallet.createRandom();
        setSeedWallet(wallet);
        setStatus('根账户创建成功！');
        setLoading(false);
      } catch (error) {
        console.error('创建根账户失败:', error);
        setStatus('创建根账户失败: ' + error.message);
        setLoading(false);
      }
    }, 1500);
  };

  // 创建灵魂账户
  const createSoulAccount = () => {
    if (!seedWallet) {
      setStatus('请先创建根账户');
      return;
    }

    setLoading(true);
    setStatus('正在创建灵魂账户...');

    setTimeout(() => {
      try {
        // 创建随机钱包作为灵魂账户
        const wallet = ethers.Wallet.createRandom();
        setSoulWallet(wallet);
        setStatus('灵魂账户创建成功！');
        setLoading(false);
      } catch (error) {
        console.error('创建灵魂账户失败:', error);
        setStatus('创建灵魂账户失败: ' + error.message);
        setLoading(false);
      }
    }, 1500);
  };

  // 认证根账户
  const certifySeedAccount = () => {
    if (!seedWallet || !soulWallet) {
      setStatus('请先创建根账户和灵魂账户');
      return;
    }

    setLoading(true);
    setStatus('正在生成环签名...');

    // 模拟环签名生成过程
    setTimeout(() => {
      setStatus('正在发送交易...');

      setTimeout(() => {
        setStatus('交易已发送，等待确认...');

        setTimeout(() => {
          setStatus('根账户认证成功！');
          setIsCertified(true);
          setLoading(false);
        }, 1500);
      }, 1500);
    }, 1500);
  };

  return (
    <Box sx={{ 
      minHeight: '100vh',
      background: 'radial-gradient(circle at 50% 50%, #1a1b3a 0%, #0a0b1e 100%)',
      pt: 10,
      pb: 8
    }}>
      <Container>
        <Typography 
          variant="h4" 
          component="h1"
          sx={{ 
            fontWeight: 'bold',
            mb: 4,
            textAlign: 'center',
            background: 'linear-gradient(45deg, #00f0ff, #00a0ff)',
            backgroundClip: 'text',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
          }}
        >
          根账户管理
        </Typography>

        <Grid container spacing={4}>
          <Grid item xs={12} md={6}>
            <GlassCard>
              <Typography variant="h6" sx={{ mb: 3 }}>
                创建根账户
              </Typography>

              {seedWallet ? (
                <Box sx={{ mb: 3 }}>
                  <TextField
                    label="根账户地址"
                    value={seedWallet.address}
                    fullWidth
                    margin="normal"
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                  <TextField
                    label="私钥"
                    value={seedWallet.privateKey}
                    fullWidth
                    margin="normal"
                    InputProps={{
                      readOnly: true,
                    }}
                    type="password"
                  />
                  <Alert severity="warning" sx={{ mt: 2 }}>
                    请安全保存您的私钥，不要分享给任何人！
                  </Alert>
                </Box>
              ) : (
                <Button
                  variant="contained"
                  color="primary"
                  fullWidth
                  onClick={createSeedAccount}
                  disabled={loading}
                  sx={{ mb: 3 }}
                >
                  {loading ? <CircularProgress size={24} /> : '创建根账户'}
                </Button>
              )}

              <Divider sx={{ my: 2 }}>
                <Chip label="灵魂账户" />
              </Divider>

              {soulWallet ? (
                <Box sx={{ mb: 3 }}>
                  <TextField
                    label="灵魂账户地址"
                    value={soulWallet.address}
                    fullWidth
                    margin="normal"
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                  <TextField
                    label="私钥"
                    value={soulWallet.privateKey}
                    fullWidth
                    margin="normal"
                    InputProps={{
                      readOnly: true,
                    }}
                    type="password"
                  />
                </Box>
              ) : (
                <Button
                  variant="contained"
                  color="secondary"
                  fullWidth
                  onClick={createSoulAccount}
                  disabled={loading || !seedWallet}
                  sx={{ mb: 3 }}
                >
                  {loading ? <CircularProgress size={24} /> : '创建灵魂账户'}
                </Button>
              )}

              <Button
                variant="contained"
                color="primary"
                fullWidth
                onClick={certifySeedAccount}
                disabled={loading || !seedWallet || !soulWallet || isCertified}
                sx={{ mt: 3 }}
              >
                {loading ? <CircularProgress size={24} /> : '认证根账户'}
              </Button>

              {status && (
                <Typography 
                  variant="body2" 
                  sx={{ 
                    mt: 2, 
                    textAlign: 'center',
                    color: isCertified ? 'success.main' : 'info.main'
                  }}
                >
                  {status}
                </Typography>
              )}
            </GlassCard>
          </Grid>

          <Grid item xs={12} md={6}>
            <GlassCard>
              <Typography variant="h6" sx={{ mb: 3 }}>
                认证详情
              </Typography>

              {isCertified ? (
                <Box>
                  <Typography variant="body2" sx={{ mb: 1 }}>
                    <strong>根账户:</strong> {seedWallet?.address}
                  </Typography>
                  <Typography variant="body2" sx={{ mb: 1 }}>
                    <strong>灵魂账户:</strong> {soulWallet?.address}
                  </Typography>
                  <Typography variant="body2" sx={{ mb: 1 }}>
                    <strong>环大小:</strong> 10
                  </Typography>
                  <Typography variant="body2" sx={{ mb: 1 }}>
                    <strong>密钥映像:</strong> 0x7f9e8d7c6b5a4321...
                  </Typography>
                  <Typography variant="body2" sx={{ mb: 1 }}>
                    <strong>交易哈希:</strong> 0x1a2b3c4d5e6f7890...
                  </Typography>
                  <Typography variant="body2" sx={{ mb: 3 }}>
                    <strong>生成时间:</strong> {new Date().toLocaleString('zh-CN', { timeZone: 'Asia/Shanghai' })}
                  </Typography>

                  <Button
                    variant="contained"
                    color="secondary"
                    fullWidth
                    onClick={() => navigate('/shadow-account')}
                  >
                    前往创建影子账户
                  </Button>
                </Box>
              ) : (
                <Typography variant="body1" sx={{ color: 'text.secondary', fontStyle: 'italic' }}>
                  完成根账户认证后，这里将显示详细信息。
                </Typography>
              )}
            </GlassCard>

            <GlassCard sx={{ mt: 4 }}>
              <Typography variant="h6" sx={{ mb: 3 }}>
                环签名说明
              </Typography>
              <Typography variant="body2" sx={{ mb: 2 }}>
                环签名技术允许您在不泄露身份的情况下证明您是某个群体的成员。在这里，我们使用环签名来证明根账户和灵魂账户之间的关联。
              </Typography>
              <Typography variant="body2">
                完成认证后，您可以创建影子账户，这些账户将与您的根账户关联，但在区块链上无法追踪这种关联。
              </Typography>
            </GlassCard>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
};

export default SeedAccountPage;
